import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom'
import axios from 'axios';
import TestRunList from './TestRunList';

export class TestCaseInfo extends Component {
    baseUrl = "http://web-api.int.testunited.minikube.local"

    state = {
        testCase: {},
        testRuns: []
      }

      componentDidMount(){
        axios.get(this.baseUrl + '/testcases/'+this.props.match.params.testCaseId)
         .then(res => this.setState({testCase: res.data}))
      }


    render() {
        const {id, name, testSourceId} = this.state.testCase;
        return (
            <div>
                Id: {id} <br/>
                Test Source Id: {testSourceId} <br/>
                Name: {name} <br/>
                {/* Result: {result? "true": "false"} <br/>
                <div style={{textAlign:"left"}}>
                <TestRunList testRuns={this.state.testRuns}/>
                </div> */}
            </div>

        )
    }
}


export default TestCaseInfo
