import React, { useContext } from 'react';
import ApplicationListItem from './ApplicationListItem';
import PropTypes from 'prop-types';
import axios from 'axios';
import Config from '../../Config';
import AppContext from '../../AppContext'
class ApplicationList extends React.Component {

  baseUrl = new Config().getApiHost();
  static contextType = AppContext;

  state = {
    applications: []
  }

  headerStyle = {
    background: '#333',
    color: '#fff',
    textAlign: 'center',
    padding: '10px'
}

  componentDidMount() {
    axios.get(this.baseUrl + '/applications')
      .then(res => this.setState({ applications: res.data }))
  }

  render() {

    return (<div style={this.headerStyle}>

          <h2>Please select an application to proceed.. </h2>
          this.state.applications.map((app) => (
            <ApplicationListItem key={app.id} item={app} />
          ))}
    </div>);
  }


}

export default ApplicationList;
